mapper.select:
SELECT e.empno , e.ename , e.job , e.mgr , e.hiredate , e.sal , e.comm , e.deptno FROM emp e
WHERE e.empno = #{empno}
ORDER BY e.ename

mapper.insert:
INSERT INTO EMP e
  ( e.empno
  , e.ename
  , e.hiredate
  , e.job
   )
VALUES
  ( #{empno}
  , #{ename}
  , sysdate
  , #{job}
 )

mapper.update:
    
        UPDATE EMP e
		   SET e.ename = #{ENAME}
		     , e.job = #{JOB}
		     , e.mgr = #{MGR}
		     , e.hiredate = #{HIREDATE}
		     , e.sal = #{SAL}
		     , e.comm = #{COMM}
		     , e.deptno = #{DEPTNO}
		 WHERE e.empno = #{EMPNO}

