mapper.update:
UPDATE EMP e
 
   
     e.ename = #{ename}
   
   
     , e.job = #{job}
   
   
     , e.mgr = #{mgr}
   
   
     , e.hiredate = TO_DATE(#{hiredate}, 'YYYY-MM-DD HH24:MI:SS') 
   
   
     , e.sal = #{sal}
   
   
     , e.comm = #{comm}
   
   
     , e.deptno = #{deptno}
   
 
 
 WHERE e.empno = #{empno}
